"use strict";window.onload=function(){},$(function(){$(".login-user").validate({rules:{username:{required:!0},password:{required:!0}},showErrors:function(e,o){0!=o.length&&$(".err-con").empty().text(o[0].message)},messages:{username:{required:"请输入用户名"},password:{required:"请输入密码"}},onfocusout:!1,errorPlacement:function(e,o){e.appendTo($(".err-con"))},submitHandler:function(e){$.ajax({url:"/login",type:"post",dataType:"json",data:$(e).serialize(),success:function(e){"1"==e.message?(document.cookie="username="+e.username,window.location.href="./index.html"):$(".err-con").html("用户名密码错误")},error:function(e){console.log(e)}})}});$(".codeLogin").click(function(){$(".login-user").hide(),$(".code").show()}),$(".acountLogin").click(function(){$(".code").hide(),$(".login-user").show()})});